int magic(int x)
{
	if(x<10)return x;
	int temp[20]={0},t=x,num=0;
	while(t)
	{
		temp[++temp[0]]=t%10;
		t/=10;
	}
	for(int i=temp[0];i>=2;i--)num=num*10+abs(temp[i]-temp[i-1]);
	return magic(num);
}
bool Check(ll x)
{
	if(magic(x)==7) return true;
	return false;
}
int Ask(int num)
{   
    int temp=0;
    int i;
	for(i=1;i<=1200;i++)
	{
	   if(i*1000000>num)break;
	   temp+=table[i];
    }
	int l=(i-1)*1000000+1,r=num;
	if(l>r)return temp;
	for(int i=l;i<=r;i++)if(magic(i)==7)temp++;
	return temp; 
}
